import MainStyledRegister from "../../Styles/mainRegister.js";
import { toast } from "react-toastify";
import { useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import HeaderRegister from "../../Components/HeaderRegister";
import Button from "../../Components/Button";
import axios from "axios";
import { useState, useEffect } from "react";

function Register() {
  const Navigate = useNavigate();
  const FormSchema = yup.object().shape({
    email: yup.string().required("email obrigatório").email(),
    password: yup.string().required("Senha obrigatória"),
    confirmePassword: yup.string().required("Confirmação de senha obrigatória"),
    name: yup.string().required("Nome obrigatório"),
    bio: yup.string().required("Bio obrigatória"),
    contact: yup.string().required("Forma de contat obrigatória"),
    module: yup.string().required("Modulo obrigatório"),
  });
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(FormSchema),
  });

  const onSubmitFunction = (e, data) => {
    e.preventDefault();
    axios
      .post("https://kenziehub.herokuapp.com/users", data)
      .then((response) => {
        // setar token no localStorage
        // verificar se email e senha correspondem
        // ajeitar botoes e css
        // redirecionar para pagina home
        toast.success("usuário criado com sucesso");
        Navigate("/");
      })
      .catch((err) => {
        toast.error("Não foi possível criar o usuário");
        console.log(err);
      });
  };

  return (
    <div>
      <HeaderRegister />
      <MainStyledRegister>
        <form onSubmit={handleSubmit(onSubmitFunction)}>
          <p>Nome:</p>
          <input
            {...register("name")}
            type="text"
            placeholder="Digite seu nome"
          />
          {errors.name?.message}
          <p>E-mail:</p>
          <input
            {...register("email")}
            type="email"
            placeholder="Digite seu e-mail"
          />
          {errors.email?.message}
          <p>Senha:</p>
          <input
            {...register("password")}
            type="password"
            placeholder="Digite sua senha"
          />
          {errors.password?.message}
          <p>Confirmação de senha:</p>
          <input
            {...register("confirmPassword")}
            type="text"
            placeholder="Confime sua senha"
          />
          {errors.confirmePassword?.message}
          <p>Bio:</p>
          <input
            {...register("bio")}
            type="text"
            placeholder="Fale um pouco sobre você"
          />
          {errors.bio?.message}
          <p>Opções de contato:</p>
          <input
            {...register("contact")}
            type="text"
            placeholder="Opção de contato"
          />
          {errors.contact?.message}
          <p>Módulos:</p>
          <select {...register("select")} placeholder="Selecione o seu módulo">
            <option value="M1">M1</option>
            <option value="M2">M2</option>
            <option value="M3">M3</option>
            <option value="M4">M4</option>
            <option value="M5">M5</option>
            <option value="M6">M6</option>
          </select>
          {errors.select?.message}

          <Button type="submit">Cadastrar</Button>
        </form>
      </MainStyledRegister>
    </div>
  );
}
export default Register;
