import DivModalStyled from "../../Styles/divModal";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import Button from "../Button";
import axios from "axios";
import { useEffect } from "react";
import { toast } from "react-toastify";
const Modal = ({ setModal, setTechArray }) => {
  const token = localStorage.getItem("@token");
  const FormSchema = yup.object().shape({
    title: yup.string().required("Nome obrigatório"),
    status: yup.string().required("Status obrigatório"),
  });
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(FormSchema),
  });

  const handleData = (data) => {
    console.log(data);
    const headers = {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    };
    axios
      .post("https://kenziehub.herokuapp.com/users/techs", data, headers)
      .then((res) => setTechArray((obj) => [...obj, ...res.data]))
      .catch((err) => {
        console.log(err);
        toast.error("não foi possível criar tecnologia");
      });
  };

  return (
    <DivModalStyled>
      <div className="modalContainer">
        <div className="headerModal">
          <p className="p-tech">Cadastrar Tecnologia</p>
          <button onClick={() => setModal(false)} className="closeModal">
            X
          </button>
        </div>
        <div className="mainModal">
          <form onSubmit={handleSubmit(handleData)}>
            <label>Nome</label>
            <input
              type="text"
              {...register("title")}
              placeholder="Nome da tecnologia"
            />
            {errors.title?.message}
            <label>Selecionar Status</label>
            <select {...register("status")}>
              <option value="Iniciante">Iniciante</option>
              <option value="Intermediário">Intermediário</option>
              <option value="Avançado">Avançado</option>
            </select>
            {errors.status?.message}
            <Button className="buttonSubmit" type="submit">
              Enviar
            </Button>
          </form>
        </div>
      </div>
    </DivModalStyled>
  );
};
export default Modal;
