import { createContext, useEffect } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import { useContext } from "react";
import { userContext } from "./UserContext";
export const techContext = createContext({});

export const TechProvider = ({ children }) => {
  const token = localStorage.getItem("@token");
  const { userData, setUserData } = useContext(userContext);
  console.log(userData);
  const handleDeleteClick = (obj) => {
    console.log(obj);
    axios
      .post(`https://kenziehub.herokuapp.com/users/tech/:${obj}.id`)
      .then((res) => console.Console.log(res))
      .catch((err) => console.log(err));
  };
  useEffect(() => {
    async function newArrayData() {
      const { newData } = await axios.get(
        "https://kenziehub.herokuapp.com/profile",
        {
          headers: { authorization: `Bearer: ${token}` },
        }
      );
      console.log(newData);
    }
  }, []);

  const handleData = (data) => {
    console.log(data);
    const headers = {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    };
    axios
      .post("https://kenziehub.herokuapp.com/users/techs", data, headers)
      .then((res) => {
        toast.success("Tecnologia criada com sucesso");
      })
      .catch((err) => {
        console.log(err);
        toast.error(`${err.response.data.message}`);
      });
  };

  return (
    <techContext.Provider
      value={{
        handleDeleteClick,
        handleData,
      }}
    >
      {children}
    </techContext.Provider>
  );
};
