import MainStyledRegister from "../../Styles/mainRegister.js";
import { toast } from "react-toastify";
import { useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import HeaderRegister from "../../Components/HeaderRegister";
import Button from "../../Components/Button";
import axios from "axios";
import { useState, useEffect } from "react";

function Register() {
  const Navigate = useNavigate();
  const FormSchema = yup.object().shape({
    email: yup.string().required("email obrigatório"),
    password: yup.string().required("Senha obrigatória"),
    confirmePassword: yup.string().required("Confirmação de senha obrigatória"),
    name: yup.string().required("Nome obrigatório"),
    bio: yup.string().required("Bio obrigatória"),
    contact: yup.string().required("Forma de contat obrigatória"),
    module: yup.string().required("Modulo obrigatório"),
  });
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(FormSchema),
  });

  const onSubmitFunction = (data) => {
    console.log(data);
    /*  axios
      .post("https://kenziehub.herokuapp.com/users", data)
      .then((response) => {
        console.log(response);
        toast.success("usuário criado com sucesso");
        Navigate("/")
      })
      .catch((err) => {
        toast.error("Não foi possível criar o usuário");
        console.log(err);
      }); */
  };

  return (
    <div>
      <HeaderRegister />
      <MainStyledRegister>
        <dic className="divForm">
          <h2>Crie sua conta</h2>
          <p className="createCount">Rápido e grátis, vamos nessa</p>
          <form
            onSubmit={(e) => {
              handleSubmit(onSubmitFunction);
            }}
          >
            <p>Nome:</p>
            <input
              {...register("name")}
              type="text"
              placeholder="Digite seu nome"
            />
            {errors.name?.message}
            <p>E-mail:</p>
            <input
              {...register("email")}
              type="email"
              placeholder="Digite seu e-mail"
            />
            {errors.email?.message}
            <p>Senha:</p>
            <input
              {...register("password")}
              type="password"
              placeholder="Digite sua senha"
            />
            {errors.password?.message}
            <p>Confirmação de senha:</p>
            <input
              {...register("confirmPassword")}
              type="password"
              placeholder="Confime sua senha"
            />
            {errors.confirmePassword?.message}
            <p>Bio:</p>
            <input
              {...register("bio")}
              type="text"
              placeholder="Fale um pouco sobre você"
            />
            {errors.bio?.message}
            <p>Opções de contato:</p>
            <input
              {...register("contact")}
              type="text"
              placeholder="Opção de contato"
            />
            {errors.contact?.message}
            <p>Módulos:</p>
            <select
              {...register("select")}
              placeholder="Selecione o seu módulo"
            >
              <option value="Primeiro módulo (Introdução ao Frontend)">
                Primeiro módulo (Introdução ao Frontend)
              </option>
              <option value="Segundo módulo (Frontend Avançado)">
                Segundo módulo (Frontend Avançado)
              </option>
              <option value="Terceiro módulo (Introdução ao Backend">
                Terceiro módulo (Introdução ao Backend)
              </option>
              <option value="Quarto módulo (Backend Avançado)">
                Quarto módulo (Backend Avançado)
              </option>
            </select>
            {errors.select?.message}

            <Button type="submit" className="buttonRegister">
              Cadastrar
            </Button>
          </form>
        </dic>
      </MainStyledRegister>
    </div>
  );
}
export default Register;
