import MainStyled from "../../Styles/main";
import { toast } from "react-toastify";
import { useNavigate } from "react-router-dom";
import { useForm } from "react-hook-form";
import { yupResolver } from "@hookform/resolvers/yup";
import * as yup from "yup";
import Header from "../../Components/Header/Header";

import Button from "../../Components/Button";
import axios from "axios";
import { useState, useEffect } from "react";

function Register() {
  const Navigate = useNavigate();
  const FormSchema = yup.object().shape({
    email: yup.string().required("email obrigatório").email(),
    password: yup.string().required("Senha obrigatória"),
  });
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(FormSchema),
  });

  const onSubmitFunction = (data) => {
    axios
      .post("https://kenziehub.herokuapp.com/users", data)
      .then((response) => {
        console.log("deu erro");
        // setar token no localStorage
        // verificar se email e senha correspondem
        // ajeitar botoes e css
        // redirecionar para pagina home
      })
      .catch((err) => {
        toast.error("usuário inexistente");
        console.log(err);
      });
  };

  const useRegister = () => {
    Navigate("/register");
  };
  return (
    <div>
      <Header />
      <MainStyled>
        <form onSubmit={handleSubmit(onSubmitFunction)}>
          <input
            {...register("email")}
            type="email"
            placeholder="Digite seu email"
          />
          {errors.email?.message}
          <input
            {...register("password")}
            type="password"
            placeholder="Digite sua senha"
          />
          {errors.password?.message}
          <Button type="submit">entrar</Button>
        </form>
        <p>Ainda não possui uma conta?</p>
        <Button onClick={useRegister}>Cadastre-se</Button>
      </MainStyled>
    </div>
  );
}
export default Register;
